// AOS

const blockPartner = document.querySelector('#partnerField')
blockPartner.style.height = document.getElementById('partner').clientHeight + 150 + 'px'

const pageWidth = document.documentElement.scrollWidth

if (pageWidth < 1199) {
    blockPartner.style.height = document.getElementById('partner').clientHeight + 125 + 'px'
}
if (pageWidth < 991) {
    blockPartner.style.height = document.getElementById('partner').clientHeight + 173 + 'px'
}
if (pageWidth < 767) {
    blockPartner.style.height = document.getElementById('partner').clientHeight + 80 + 'px'
}
if (pageWidth < 424) {
    blockPartner.style.height = document.getElementById('partner').clientHeight + 155 + 'px'
}

function openMenu(event) {
    event.preventDefault()
    const btnMenu = document.querySelector('.menu__btn');
    const listMenu = document.querySelector('.header__menu__list');
    const btnElement = document.querySelector('.menu__btn-element');
    btnMenu.classList.toggle('menu__btn--close');
    listMenu.classList.toggle('header__menu__list--active');
    btnElement.classList.toggle('menu__btn-element_close');
}

function openPopup(event) {
  event.preventDefault()
  const popup = document.querySelector('.popup');
  popup.classList.toggle('popup--open');
  const titlePopup = document.querySelector('#popupTitle')
  if (titlePopup === null) {
    document.querySelector('.popup__title').innerHTML = '<span id="popupTitle">Отправьте сообщение и наш специалист свяжется с вами</span>';
  }
}

new Typed('#about-title',{
    strings : ['Компания','TOO Tool World'],
    typeSpeed : 240,
    delaySpeed : 80,
    loop : true
});

new Typed('#popupTitle',{
  strings : ['Если вам нужна помощь','Отправьте сообщение и наш специалист свяжется с вами'],
  typeSpeed : 40,
  delaySpeed : 80,
  loop : true
});

const anchors = document.querySelectorAll('.header__menu__list__link')

for (let anchor of anchors) {
  anchor.addEventListener('click', function (e) {
    e.preventDefault()
    
    const blockID = anchor.getAttribute('href').substr(1)
    
    document.getElementById(blockID).scrollIntoView({
      behavior: 'smooth',
      block: 'start'
    })

    const btnMenu = document.querySelector('.menu__btn')
    const listMenu = document.querySelector('.header__menu__list')
    const btnElement = document.querySelector('.menu__btn-element')
    btnMenu.classList.remove('menu__btn--close')
    listMenu.classList.remove('header__menu__list--active')
    btnElement.classList.remove('menu__btn-element_close')
  })
}

window.onscroll = function() {fixHeader()}

let header = document.querySelector('.header')
let logo = document.querySelector('.logo')
let sticky = header.offsetTop
let bodyTop = document.querySelector('body')

function fixHeader() {
  if (window.pageYOffset > sticky) {
    header.classList.add("header--sticky")
    logo.classList.add("logo--sticky")
    bodyTop.classList.add("body--top")
  } else {
    header.classList.remove("header--sticky")
    logo.classList.remove("logo--sticky")
    bodyTop.classList.remove("body--top")
  }
}

[].forEach.call(document.querySelectorAll('input[type="tel"]'), function (input) {
  let keyCode;
  function mask(event) {
    event.keyCode && (keyCode = event.keyCode);
    let pos = this.selectionStart;
    if (pos < 3) event.preventDefault();
    let matrix = '+7 (___) ___-____',
      i = 0,
      def = matrix.replace(/\D/g, ''),
      val = this.value.replace(/\D/g, ''),
      new_value = matrix.replace(/[_\d]/g, function (a) {
        return i < val.length ? val.charAt(i++) || def.charAt(i) : a
      });
    i = new_value.indexOf('_');
    if (i != -1) {
      i < 5 && (i = 3);
      new_value = new_value.slice(0, i)
    }
    var reg = matrix.substr(0, this.value.length).replace(/_+/g,
      function (a) {
        return '\\d{1,' + a.length + '}'
      }).replace(/[+()]/g, '\\$&');
    reg = new RegExp('^' + reg + '$');
    if (!reg.test(this.value) || this.value.length < 5 || keyCode > 47 && keyCode < 58) this.value = new_value;
    if (event.type == 'blur' && this.value.length < 5) this.value = ''
  }
  input.addEventListener('input', mask, false);
  input.addEventListener('focus', mask, false);
  input.addEventListener('blur', mask, false);
  input.addEventListener('keydown', mask, false);
});

const btn = document.querySelector('.form__submit')
	
	btn.onclick = (e) => {
		e.preventDefault()
		let x = document.querySelector('#firstname').value
		if (x === "") {
            document.querySelector('.form__error').textContent = "Укажите Ваше имя";
            return false;
        }
		x = document.querySelector('#phone').value
		if (x === "") {
            document.querySelector('.form__error').textContent = "Укажите Ваш номер телефона";
            return false;
        } else {
            let re = /^((8|\+7)[\- ]?)?(\(?\d{3,4}\)?[\- ]?)?[\d\- ]{5,10}$/g;
            if(!re.test(x)){
                document.querySelector('.form__error').textContent = "Укажите номер телефона в формате +7(XХХ)ХХХХХХХ";
                return false;
            }
        }
		const params = new FormData(document.querySelector('.form'))
		
		fetch('./send.php', {
			method: 'POST',
			body: params
		}).then(
			response => {
				return response.text()
		}).then(
			() => {
        document.querySelector('.form__error').textContent = ''
        document.getElementById('popupTitle').remove();
				document.querySelector('.popup__title').textContent = 'Сообщение успешно отправлено';
				setTimeout( function(){ 
					document.querySelector('.form').reset()
				}, 2000)
		}
		)
	}